import { Card, Button, Popover } from "antd";
import {
  EllipsisOutlined,
  HeartOutlined,
  MessageOutlined,
  RetweetOutlined,
} from "@ant-design/icons";

const PostCard = () => {
  return (
    <div>
      <Card
        cover={post.Images[0] && <post.Images images={post.Images} />}
        actions={[
          <RetweetOutlined />,
          <HeartOutlined />,
          <MessageOutlined />,
          <Popover>
            <EllipsisOutlined />
          </Popover>,
        ]}
      >
        <Image />
        <Content />
        <Button></Button>
      </Card>
      <CommentForm />
      <Comments />
    </div>
  );
};

export default PostCard;
