import { useCallback, useState } from "react";
import Head from "next/head";
import { Form, Input, Checkbox, Button } from "antd";
import styled from "styled-components";

import AppLayout from "../components/AppLayout";
import userInput from "../hooks/userInput";

const ErrorMessage = styled.div`
  color: red;
`;

const Signup = () => {
  const [id, setId] = userInput("");
  const [nickname, setNickname] = userInput("");
  const [password, setPassword] = userInput("");
  const [passwordConfirm, setPasswordConfirm] = userInput("");
  const [passwordError, setPasswordError] = userInput(false);

  const onChangePasswordConfirm = useCallback((e) => {
    setPasswordConfirm(e.target.value);
    setPasswordError(e.target.value !== password);
  });

  const onSubmit = useCallback(() => {}, []);

  return (
    <>
      <Head>
        <title>Node Bird | 회원가입</title>
      </Head>

      <AppLayout>
        <Form onFinish>
          <div>
            <label htmlFor="user-id">아이디</label>
            <br />
            <Input
              name="user-id"
              value={id}
              required
              onChange={onChangeId}
            ></Input>
          </div>
          <div>
            <label htmlFor="user-nickname">닉네임</label>
            <br />
            <Input
              name="user-nickname"
              value={nickname}
              required
              onChange={onChangeNickname}
            ></Input>
          </div>
          <div>
            <label htmlFor="user-password">비밀번호</label>
            <br />
            <Input
              type="password"
              name="user-password"
              value={password}
              required
              onChange={onChangePassword}
            ></Input>
          </div>
          <div>
            <label htmlFor="user-password-confirm">비밀번호 확인</label>
            <br />
            <Input
              type="password"
              name="user-password-confirm"
              value={passwordConfirm}
              required
              onChange={onChangePasswordConfirm}
            ></Input>
            {passwordError && (
              <ErrorMessage> 비밀번호가 일치하지 않습니다.</ErrorMessage>
            )}
          </div>
        </Form>
      </AppLayout>
    </>
  );
};

export default Signup;
